import React from 'react'
import {FaTimes} from 'react-icons/fa'

const Task = ({ task}) => {
  return (
    <div className='task'>
        <h3>{task.text} <FaTimes style={{cursor: 'pointer', color:'red'}}/></h3>
        <p>{task.day}</p>
    </div>
  )
}

export default Task
